  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'First bootcamp';

   public book: Book ={
    title: 'MyBook',
    author: 'Jas Kaur',
    abstract: 'Book my fav places',
   }

}
